mapper.select:
WITH RECURSIVE MENU_3 (MENU_ID, MENU_NM, UP_MENU_ID, MENU_DEPTH, MENU_URL, VISIBLE_YN, SORT_SEQ, ETC) AS ( SELECT MENU_1.MENU_ID, MENU_1.MENU_NM, MENU_1.UP_MENU_ID, MENU_1.MENU_DEPTH, MENU_1.MENU_URL, MENU_1.VISIBLE_YN, MENU_1.SORT_SEQ, MENU_1.ETC FROM CM_MENU MENU_1 WHERE MENU_1.UP_MENU_ID = #{menuId}
UNION ALL
SELECT MENU_2.MENU_ID, MENU_2.MENU_NM, MENU_2.UP_MENU_ID, MENU_2.MENU_DEPTH, MENU_2.MENU_URL, MENU_2.VISIBLE_YN, MENU_2.SORT_SEQ, MENU_2.ETC FROM CM_MENU MENU_2 INNER JOIN MENU_3 ON MENU_2.MENU_ID = MENU_3.UP_MENU_ID ) SELECT * FROM MENU_3 WHERE 1 = 1 GROUP BY MENU_ID ORDER BY MENU_3.MENU_DEPTH , MENU_3.MENU_ID , MENU_3.SORT_SEQ

mapper.insert:
        INSERT INTO CM_MENU (
			MENU_ID
            , FST_CRER_ID
            , FST_CRE_DTM
            , AUDIT_ID
            , AUDIT_DTM
            , UP_MENU_ID
            , MENU_DEPTH
            , MENU_NM
            , MENU_URL
            , VISIBLE_YN
            , SORT_SEQ
            , ETC
        )
        VALUES(
        	#{menuId}
        	, #{ssLoginId}
        	, NOW()
        	, #{ssLoginId}
        	, NOW()
        	, #{upMenuId}
        	, #{menuDepth}
        	, #{menuNm}
        	, #{menuUrl}
        	, #{visibleYn}
        	, #{sortSeq}
        	, #{etc}
        );

mapper.update:
UPDATE CM_MENU
   SET UP_MENU_ID = #{upMenuId}
     , MENU_DEPTH = #{menuDepth}
     , MENU_NM = #{menuNm}
     , MENU_URL = #{menuUrl}
     , VISIBLE_YN = #{visibleYn}
     , SORT_SEQ = #{sortSeq}
     , ETC = #{etc}
	 , AUDIT_ID = #{ssLoginId}
	 , AUDIT_DTM = NOW()
 WHERE 1 = 1
   AND MENU_ID = #{menuId}

mapper.delete:
DELETE FROM CM_ROLE_MENU
 WHERE 1 = 1
   AND MENU_ID = #{menuId}

mapper.insert:
        INSERT INTO CM_ROLE_MENU (
			ROLE_ID
			, MENU_ID
            , FST_CRER_ID
            , FST_CRE_DTM
            , AUDIT_ID
            , AUDIT_DTM
        )
        VALUES(
        	#{roleId}
        	, #{menuId}
        	, #{ssLoginId}
        	, NOW()
        	, #{ssLoginId}
        	, NOW()
        );
