mapper.insert:

	SELECT LPAD(IFNULL(MAX(FILE_ID), 0) + 1, 3, '0') AS FILE_ID
	  FROM CM_FILE
	 WHERE 1 = 1
	   AND CHNL_ID = #{chnlId}
	   AND TARGET_ID = #{targetId}
	   AND TARGET_GUBUN = #{targetGubun}

INSERT INTO CM_FILE (
	CHNL_ID
	, TARGET_ID
	, TARGET_GUBUN
	, FILE_ID
	, FST_CRER_ID
	, FST_CRE_DTM
	, AUDIT_ID
	, AUDIT_DTM
	, FILE_NM
	, FILE_OG_NM
)
VALUES (
	#{chnlId}
	, #{targetId}
	, #{targetGubun}
	, #{fileId}
	, #{ssLoginId}
	, NOW()
	, #{ssLoginId}
	, NOW()
	, #{fileNm}
	, #{fileOgNm}
)

mapper.delete:
DELETE FROM CM_FILE
 WHERE CHNL_ID = #{chnlId}
   AND TARGET_ID = #{targetId}
   AND TARGET_GUBUN = #{targetGubun}
   AND FILE_ID = #{fileId}

mapper.select:
SELECT CHNL_ID
     , TARGET_ID
     , TARGET_GUBUN
     , FILE_ID
     , FILE_NM
     , FILE_OG_NM
  FROM CM_FILE
 WHERE CHNL_ID = #{chnlId}
   AND TARGET_ID = #{targetId}
   AND TARGET_GUBUN = #{targetGubun}
